import { useLocation } from 'react-router-dom'
import './singlePost.css'
import { useEffect, useState } from 'react'
import axios from 'axios'

export default function SinglePost() {
  const location = useLocation()
  const path = location.pathname.split('/')[2]
  const [post,setPost] = useState({})

  useEffect(() =>{
    const getPost = async () =>{
      const res = await axios.get("/posts" + path)
      setPost(res.data)
    }
    getPost()
  },[path])
  return (
    <div className='singlePost'>
      <div className="singlePostWrapper">
        {post.photo && (
        <img 
          src={post.photo}
          alt="" 
          className='singlePostImg'
          />
        )}
          <h1 className="singlePostTitle">{post.title}
            <div className="singlePostEdit">
            <i className="singlePostIcon fa-regular fa-pen-to-square"></i>
            <i className="singlePostIcon fa-regular fa-trash-can"></i>
            </div>
          </h1>
          <div className="singlePostInfo">
            <span className='singlePostAuthor'>Author: <b>{post.username}</b></span>
            <span className='singlePostDate'>1 hour ago</span>
          </div>
          <p className='singlePostDesc'>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cum, magnam quos. Odio, reiciendis dolores? Adipisci ex suscipit cupiditate reprehenderit libero eum dolor, eveniet necessitatibus inventore, quod magni, repellat error totam?
