// src/components/sidebar/Sidebar.jsx

import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Sidebar.css";

export default function Sidebar() {
  const [cats, setCats] = useState([
    { _id: "1", name: "Travel" },
    { _id: "2", name: "Tech" },
    { _id: "3", name: "Sports" },
    { _id: "4", name: "Food" },
    { _id: "5", name: "Health" },
    { _id: "6", name: "Fitness" },
  ]);
  const [error, setError] = useState(false);

  // useEffect(() => {
  //   const getCats = async () => {
  //     try {
  //       const res = await axios.get("http://localhost:5000/categories"); // Ensure this URL matches your server's URL
  //       if (Array.isArray(res.data)) {
  //         setCats(res.data);
  //       } else {
  //         console.error("API response is not an array:", res.data);
  //       }
  //     } catch (err) {
  //       setError(true);
  //       console.error("Error fetching categories:", err);
  //     }
  //   };
  //   getCats();
  // }, []);

  return (
    <div className="sidebar">
      <div className="sidebarItem">
        <span className="sidebarTitle">ABOUT ME</span>
        <img
          src="https://images.pexels.com/photos/5052867/pexels-photo-5052867.jpeg?auto=compress&cs=tinysrgb&w=600"
          alt="Profile"
        />
        <p>
          Hello, I'm Rishav Raj, a passionate tech lover currently pursuing B.Tech at BIT Mesra. Beyond my studies, I find joy in writing informative and engaging content. This blog is where I share insights, experiences, and tips on all things tech. Join me as we explore the latest trends, learn together, and uncover useful knowledge that empowers us in the digital age. Let's connect and dive into the world of technology!
        </p>
      </div>
      <div className="sidebarItem">
        <span className="sidebarTitle">CATEGORIES</span>
        {error ? (
          <p>Error fetching categories</p>
        ) : (
          <ul className="sidebarList">
            {cats.map((c) => (
              <Link to={`/category/${c.name}`} className="link" key={c._id}>
                <li className="sidebarListItem">{c.name}</li>
              </Link>
            ))}
          </ul>
        )}
      </div>
      <div className="sidebarItem">
        <span className="sidebarTitle">FOLLOW US</span>
        <div className="sidebarSocial">
          <i className="sidebarIcon fab fa-facebook-square"></i>
          <i className="sidebarIcon fab fa-twitter-square"></i>
          <i className="sidebarIcon fab fa-pinterest-square"></i>
          <i className="sidebarIcon fab fa-instagram-square"></i>
        </div>
      </div>
    </div>
  );
}
